import React, { useState, useEffect } from "react";

import { useParams } from "react-router-dom";

import { useSelector } from "react-redux";
import axios from "axios";

function ArrivalInfo() {
  let { id } = useParams();
  const StationIdData = useSelector((state) => state.StationNameData.stationId);

  const [timeData, setTimeData] = useState([]);

  const callApi = async () => {
    await axios.get(`http://localhost:8000/arrivalinfo/:${id}`).then((res) => {
      let resultList = res.data.response.body.items.item;
      setTimeData(resultList);
    });
  };

  useEffect(() => {
    callApi();
  }, []);

  //----------------------------------------

  // console.log(timeData)

  const currentHour = new Date().getHours();
  const currentDay = new Date().getDay();
  const currentMinute = new Date().getMinutes();
  const currentTimeStamp = currentHour * 60 + currentMinute;

  const resStNum = timeData.filter((data)=> data.stNum === Number(id));
  const resDay = resStNum.filter((data)=> 0 < currentDay && currentDay < 6 ? data.dayType === 0 : data.dayType === 1);
  const resDrctUp = resDay.filter((data)=> data.drctType === 0);
  const stationMinuteUp = resDrctUp.map((data)=>data?.tmList.split(' '));
  const stationHourUp = resDrctUp.map(data => data.tmZone);
  const stationHourUptoMin = stationHourUp.map(arr => arr*60);

  const stationTimeStamp = stationMinuteUp.map((subArray) => subArray.map(value => parseInt(value) + stationHourUptoMin[subArray.indexOf(value)]));

  console.log(stationMinuteUp);

  // 남은 시간을 계산하는 함수
const calculateRemainingTime = (hour, minute) => {
  const currentIndex = timeArray.indexOf(hour);
  const minuteArrayForHour = minuteArray[currentIndex];

  for (let i = 0; i < minuteArrayForHour.length; i++) {
    const currentMinuteValue = parseInt(minuteArrayForHour[i]);
    if (currentMinuteValue > minute) {
      const remainingMinutes = currentMinuteValue - minute;
      return { hour, minute: remainingMinutes };
    }
  }

  // 현재 시간 이후 도착 정보가 없으면 다음 시간의 첫 번째 도착 정보를 표시
  const nextHourIndex = (currentIndex + 1) % timeArray.length;
  const nextHour = timeArray[nextHourIndex];
  const nextMinuteValue = parseInt(minuteArray[nextHourIndex][0]);
  const remainingMinutes = (60 - minute) + nextMinuteValue;

  return { hour: nextHour, minute: remainingMinutes };
};

// 남은 시간 계산 및 출력
const remainingTime = calculateRemainingTime(currentHour, currentMinute);
console.log(`남은 시간: ${remainingTime.hour}시간 ${remainingTime.minute}분`);

  return (
    <>
      <h1>{StationIdData[id-101].name}</h1>
      <h2> {currentHour} 시</h2>
      {/* <h2>{timeData[0]?.tmList.split(" ")[0]} 분</h2> */}
    </>
  );
}

export default ArrivalInfo;
