import React, { useState, useEffect } from "react";

import { useParams, useLocation } from "react-router-dom";

import { useSelector } from "react-redux";
import axios from "axios";

function ArrivalInfo() {
  let { id } = useParams();
  let StationCodeData = useSelector((state) => state.StationData.stationCode);

  const [timeData, setTimeData] = useState([]);

  const location = useLocation(); // location 추가


  const callApi = async () => {
    // URL에서 drctType를 받아오기
    const urlParams = new URLSearchParams(location.search);
    const drctTypes = urlParams.getAll("drctType"); // getAll을 사용하여 배열로 받아오기
    await axios.get(`http://localhost:8000/arrivalinfo/${id}?drctType=${drctTypes.join(",")}`).then((res) => {
      let resultList = res.data.response.body.items.item;
        console.log(resultList);
      setTimeData(resultList);
    });
  };

  useEffect(() => {
    callApi();
  }, [location.search]);

  const currentHour = new Date().getHours();
  // console.log(timeData.filter(arrival => arrival.tmZone === currentHour));

  return (
    <>
      <h1>{StationCodeData[id-101].name}</h1>
      <h2> {currentHour} 시</h2>
      {/* <h2>{timeData[0]?.tmList.split(" ")[0]} 분</h2> */}
    </>
  );
}

export default ArrivalInfo;
