import styled from "styled-components";

export const ArrivalInfo = styled.div`
//   background-color: #95dd56;
background-color: #eee;
  max-width: 480px;
  height: auto;
  margin: 0 auto;
  margin-top: 5rem;
  display: flex;
  flex-flow: column;

  a {
    position:fixed;
    top:3%;
  }

  h1 {
    margin-top: 10rem;
    font-size: 5rem;
  }

  .info-inner-wrap {
    margin-top: 15rem;
    margin-bottom: 20rem;
    display: flex;
    flex-flow: row wrap;
    justify-content: center;
    text-align: center;

    h3{
        font-size:3rem;
        width:50%;
    }

    span {
        font-size:8rem;
        font-weight:600;
        width:50%;
    }
  }
`;
