import styled from "styled-components";

export const Main = styled.main`
background-color: #95dd56;
  max-width: 480px;
  height: auto;
  margin: 0 auto;
  margin-top: 5rem;
  margin-bottom: 5rem;
  display: flex;
  flex-flow: column;

  h1 {
    margin-top: 3rem;
  }

  .station-list-wrap{
    margin-top: 5rem;
    display:flex;
    flex-flow:wrap;
    justify-content:flex-start;
  }
`;

export const StationList = styled.div`
  background-color: #eee;
  width: 6.3rem;
  height: 6.3rem;
  margin: 2%;
  text-align:center;
  display:flex;
  border-radius: 10%;
  transition: 0.2s ease-in-out;
  &:hover{
    background-color: rgba(0,0,0,0.3);
    transition: 0.2s ease-in-out;
  }

    .list-item{
        font-size:1.9em;
        font-weight:600;
        width:100%;
        height:100%;
        flex-shrink:1;
        display:flex;
        justify-content:center;
        align-items:center;
    }
`;
