import React, { useState, useEffect } from "react";

import { useParams } from "react-router-dom";

import { useSelector } from "react-redux";
import axios from "axios";

function ArrivalInfo() {
  let { id } = useParams();
  let StationIdData = useSelector((state) => state.StationNameData.stationId);

  const [timeData, setTimeData] = useState([]);

  const callApi = async () => {
    await axios.get(`http://localhost:8000/arrivalinfo/:${id}`).then((res) => {
      let resultList = res.data.response.body.items.item;
      setTimeData(resultList);
    });
  };

  useEffect(() => {
    callApi();
  }, [callApi()]);

  //----------------------------------------

  // console.log(timeData)

  const currentHour = new Date().getHours();
  const currentDay = new Date().getDay();

  const resStNum = timeData.filter((data)=>data.stNum === Number(id));
  const resHour = resStNum.filter((data)=>data.tmZone === currentHour);
  const resDay = resHour.filter((data)=> 0 < currentDay && currentDay < 6 ? data.dayType === 0 : data.dayType === 1);

  console.log(resDay);

  return (
    <>
      <h1>{StationIdData[id-101].name}</h1>
      <h2> {currentHour} 시</h2>
      {/* <h2>{timeData[0]?.tmList.split(" ")[0]} 분</h2> */}
    </>
  );
}

export default ArrivalInfo;
