import styled from "styled-components";

export const Main = styled.main`
  max-width: 480px;
  height: auto;
  margin: 0 auto;
  margin-top: 5rem;
  display: flex;
  flex-flow: column;

  h1 {
    text-align: center;
    font-size: 5rem;
  }

  .station-list-wrap{
    margin-top: 4rem;
    display:flex;
    flex-flow:wrap;
    justify-content:flex-start;
  }
`;

export const StationList = styled.div`
  background-color: skyblue;
  width: 21%;
  height: 5rem;
  margin: 2%;
  text-align:center;
  display:flex;
  border-radius: 10%;


    .list-item{
        font-size:30px;
        width:100%;
        height:100%;
        flex-shrink:1;
        display:flex;
        justify-content:center;
        align-items:center;
    }
`;
