import React from "react";

import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

function Main() {
  let state = useSelector((state) => state);
  let data = state.StationStoreData.stationData;

  return (
    <>
      <h1>대전 지하철 NOW</h1>
      <span>당신은 지하철을 탈 수 있을까요?</span>
      {data.map((e, i) => (
        <StationList key={i} data={data[i]} i={i} />
      ))}
    </>
  );
}

function StationList(props) {
  return (
    <>
      <ul>
        <li>
          <Link to={`detail/`+props.i}> {props.data.name} </Link>
        </li>
      </ul>
    </>
  );
}

export default Main;
