  margin-top: 5rem;
  display: flex;
  flex-flow: column;
  position:relative;

  a {
    position:fixed;
    top: 8%;
    left: 9%;
    color:#fff;
  }

  h1 {
    margin-top: 12rem;
    font-size: 5.5rem;
  }

  .prevArrivalInfo, .nextArrivalInfo {
    position:absolute;
  }

  .prevArrivalInfo {

  }

  .nextArrivalInfo {
    right:0;
  }

  .info-inner-wrap {
    margin-top: 12rem;
    margin-bottom: 10rem;
