import {configureStore, createSlice} from '@reduxjs/toolkit';
import stationCode from './data/stationCode';

const StationStoreData = createSlice({
    name: 'StationData',
    initialState : {stationCode},
})

// export const getName = StationData.actions;

export default configureStore({
    reducer : {
        StationData: StationStoreData.reducer
    }
})
