import styled from "styled-components";

export const Main = styled.main`
    max-width:480px;
    border: 1px solid #000;
    margin: 0 auto;
    margin-top: 5rem;

    h1 {
        text-align: center;
        font-size:5rem;
    }

    span {
        width: auto;
        margin: 0 auto;
    }
`
