import styled from "styled-components";

export const ArrivalInfo = styled.div`
  background-color: #95dd56;
  max-width: 480px;
  height: auto;
  margin: 0 auto;
  margin-top: 5rem;
  display: flex;
  flex-flow: column;

  .station-list-wrap {
    margin-top: 4rem;
    display: flex;
    flex-flow: wrap;
    justify-content: flex-start;
  }
`;
