import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import axios from "axios";

function ArrivalInfo() {
  // URL 파라미터에서 id 추출
  let { id } = useParams();

  // Redux에서 역 정보 가져오기
  const StationIdData = useSelector((state) => state.StationNameData.stationId);

  // 역 도착 정보를 담을 state
  const [timeData, setTimeData] = useState([]);

  // 역 도착 정보 API 호출 함수
  const callApi = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/arrivalinfo/:${id}`);
      const resultList = response.data.response.body.items.item;
      setTimeData(resultList);
    } catch (error) {
      console.error("Error fetching arrival info:", error);
    }
  };

  // 컴포넌트가 마운트되거나 id가 변경될 때 API 호출
  useEffect(() => {
    callApi();
  }, [id]);

  // 현재 시간 정보 가져오기
  const currentHour = new Date().getHours();
  const currentMinute = new Date().getMinutes();

  // 해당 역과 방향에 대한 도착 정보 필터링
  const resStNum = timeData.filter((data) => data.stNum === Number(id));
  const resDrctUp = resStNum.filter((data) => data.drctType === 0);

  // 도착 시간과 분을 담을 배열 생성
  const stationMinuteUp = resDrctUp.map((data) => {
    // data.tmList의 형식이 문자열이면 분리된 배열을 반환하고, 아니면 빈 배열을 반환
    return typeof data.tmList === 'string' ? data.tmList.split(' ') : [];
  });
  const stationHourUp = resDrctUp.map((data) => data.tmZone);

  // 남은 도착 시간을 계산하는 함수
  const calculateRemainingTime = (hour, minute) => {
    // 현재 시간대에 해당하는 시간의 인덱스 찾기
    const currentIndex = stationHourUp.indexOf(hour);

    // 인덱스가 유효하지 않거나 해당 시간대에 도착 정보가 없으면 null 반환
    if (currentIndex === -1 || !stationMinuteUp[currentIndex]) {
      return null;
    }

    // 해당 시간대의 분 정보 배열 가져오기
    const minuteArrayForHour = stationMinuteUp[currentIndex];

    // 남은 도착 시간 계산
    const remainingTime = minuteArrayForHour.map((arrivalMinute) => {
      // 현재 분보다 나중에 도착하는 시간 찾기
      const currentMinuteValue = parseInt(arrivalMinute);
      if (currentMinuteValue > minute) {
        return { hour, minute: currentMinuteValue - minute };
      }
      return null;
    }).filter(Boolean);

    // 남은 도착 시간이 있는 경우 첫 번째 값 반환
    if (remainingTime.length > 0) {
      return remainingTime[0];
    }

    // 다음 시간대로 넘어가기
    const nextHourIndex = (currentIndex + 1) % stationHourUp.length;
    const nextHour = stationHourUp[nextHourIndex];
    const nextMinuteValue = parseInt(stationMinuteUp[nextHourIndex][0]);
    const nextHourRemainingMinutes = (60 - minute) + nextMinuteValue;

    // 다음 시간대의 남은 도착 시간 반환
    return { hour: nextHour, minute: nextHourRemainingMinutes };
  };

  // 남은 도착 시간 계산
  const remainingTime = calculateRemainingTime(currentHour, currentMinute);

  console.log(`남은 시간: ${remainingTime ? remainingTime.minute : '도착정보를 기다리는 중..'}분`);

  // 화면에 출력
  return (
    <>
      <h1>{StationIdData[id - 101]?.name}</h1>
      <h2> {remainingTime ? remainingTime.minute : '도착정보를 기다리는 중...'}</h2>
    </>
  );
}

export default ArrivalInfo;
