import React, { useState, useEffect } from "react";

import { useParams } from "react-router-dom";

import { useSelector } from "react-redux";
import axios from "axios";

function ArrivalInfo() {
  let { id } = useParams();
  let StationIdData = useSelector((state) => state);
  console.log(StationIdData);

  const [timeData, setTimeData] = useState([]);

  const callApi = async () => {
    await axios.get(`http://localhost:8000/arrivalinfo/:${id}`).then((res) => {
      let resultList = res.data.response.body.items.item;
      setTimeData(resultList);
    });
  };

  useEffect(() => {
    callApi();
  }, []);

  console.log(timeData)

  const currentHour = new Date().getHours();
  // console.log(timeData.filter(arrival => arrival.tmZone === currentHour));

  return (
    <>
      {/* <h1>{StationIdData[id-101].name}</h1> */}
      <h2> {currentHour} 시</h2>
      {/* <h2>{timeData[0]?.tmList.split(" ")[0]} 분</h2> */}
    </>
  );
}

export default ArrivalInfo;
