import styled from "styled-components";

export const Main = styled.main`
  max-width: 480px;
  height: 95vh;
  margin: 0 auto;
  margin-top: 5rem;
  display: flex;
  flex-flow: column;

  h1 {
    text-align: center;
    font-size: 5rem;
  }

  .station-list-wrap{
    background:gold;
    display:flex;
  }
`;

export const StationList = styled.div`
  background-color: skyblue;
  width: 5rem;
  height: 5rem;
  margin: 2%;
    .list-item{
        font-size:35px;
    }
`;
